<script>import GeneralObserver from './general-observer.svelte';
export let width = '100';
export let height = '500';
export let codePenId = '';
export let tabs = 'result';
export let clickToLoad = true;
export let editable = true;
export let theme = 'default';
const baseUrl = `https://codepen.io/team/codepen/embed`;
const src = `${baseUrl}/${clickToLoad ? '/preview' : ''}/${codePenId}/?height=${height}&theme-id=${theme}&default-tab=${tabs}&editable=${editable}`;
</script>

<GeneralObserver {height} {width}>
  <iframe
    {height}
    class="code-pen-sveltekit-embed"
    title={`codepen-${codePenId}`}
    {src}
    frameborder="no"
    allowfullscreen
    style={`
      width: 100%;
    `}
  />
</GeneralObserver>
