<script>import GeneralObserver from './general-observer.svelte';
export let width = '576';
export let height = '420';
export let username = '';
export let title = '';
export let byline = 'hidden';
export let share = 'hidden';
export let style = 'dark';
let baseUrl = `https://slides.com/${username}/${title}/embed?`;
const config = {
    style,
    byline,
    share,
};
const queryString = new URLSearchParams(config);
const src = `${baseUrl}&${queryString.toString()}`;
</script>

<GeneralObserver {height} {width}>
  <iframe
    {src}
    {width}
    {height}
    {title}
    scrolling="no"
    frameborder="0"
    allowfullscreen
    style="width: 100%;"
  />
</GeneralObserver>
